// import currenciesAPI from '../services/currenciesAPI';

// Coloque aqui suas actions
export const SAVE_EMAIL = 'SAVE_EMAIL';

export const saveEmail = (email) => ({
  type: SAVE_EMAIL,
  payload: {
    email,
  },
});

export const FETCH_CURRENCIES = 'FETCH_CURRENCIES';

export const receivedCurrencies = (currencies) => ({
  type: FETCH_CURRENCIES,
  payload: {
    currencies,
  },
});

export const thunkCurrencies = () => async (dispatch) => {
  try {
    const response = await fetch('https://economia.awesomeapi.com.br/json/all');
    const json = await response?.json();
    if (json) {
      const initialsCurrency = Object
        .keys(json).filter((currency) => currency !== 'USDT');

      dispatch(receivedCurrencies(initialsCurrency));
    }
  } catch (error) {
    console.log(error);
  }
};

export const SAVE_EXPENSE = 'SAVE_EXPENSE';

export const saveExpense = (expense) => ({
  type: SAVE_EXPENSE,
  payload: {
    expense,
  },
});

export const thunkPrice = () => async (dispatch) => {
  try {
    const respose = await fetch('https://economia.awesomeapi.com.br/json/all');
    const json = await response?.json();
    if (json) {
      const initialsCurrency = Object
        .keys(json).filter((currency) => currency !== 'USDT');

      dispatch(receivedCurrencies(initialsCurrency));
    }
  } catch (error) {
    console.log(error);
  }
};
