import React from 'react';
import { connect } from 'react-redux';
import propTypes, { string } from 'prop-types';

class ExpenseForm extends React.Component {
  state = {
    value: '',
    desc: '',
    currency: '',
    payment: '',
    tag: '',
  }

  render() {
    const { currencies } = this.props;
    return (
      <form>
        <label htmlFor="value-input">
          Valor:
          <input type="text" data-testid="value-input" id="value-input" />
        </label>

        <label htmlFor="description-input">
          Descrição:
          <input type="text" data-testid="description-input" id="description-input" />
        </label>

        <label htmlFor="currencies">
          Moeda:
          <select name="currencies" id="currencies">
            {currencies.map((currency) => <option key={ currency }>{currency}</option>)}
          </select>
        </label>

        <label htmlFor="method-input">
          Metódo de pagamento:
          <select name="method-input" id="method-input" data-testid="method-input">
            <option>Dinheiro</option>
            <option>Cartão de crédito</option>
            <option>Cartão de débito</option>
          </select>
        </label>

        <label htmlFor="tag-input">
          Tag:
          <select name="tag-input" id="tag-input" data-testid="tag-input">
            <option>Alimentação</option>
            <option>Lazer</option>
            <option>Trabalho</option>
            <option>Transporte</option>
            <option>Saúde</option>
          </select>
        </label>

        <button type="button">Adicionar Despesa</button>
      </form>
    );
  }
}

const mapStateToProps = (state) => ({
  currencies: state.wallet.currencies,
});

ExpenseForm.propTypes = {
  currencies: propTypes.arrayOf(string).isRequired,
};

export default connect(mapStateToProps)(ExpenseForm);
