// Esse reducer será responsável por tratar o todas as informações relacionadas as despesas

import { FETCH_CURRENCIES, SAVE_EXPENSE, TOTAL_PRICE } from '../actions';

const INITIAL_STATE = {
  currencies: [],
  expenses: [],
  editor: false,
  idToEdit: 0,
};

export const walletReducer = (state = INITIAL_STATE, action) => {
  switch (action?.type) {
  case FETCH_CURRENCIES:
    return {
      ...state,
      currencies: action.payload.currencies,
    };
  case SAVE_EXPENSE:
    return {
      ...state,
      expenses: [...state.expenses, action.payload.expense],
    };
  default:
    return state;
  }
};

const INITIAL_STATE_TOTAL = {
  total: 0,
};

export const totalPriceReducer = (state = INITIAL_STATE_TOTAL, action) => {
  switch (action?.type) {
  case TOTAL_PRICE:
    console.log(action.payload.price);
    return {
      ...state,
      total: 2,
    };
  default:
    return state;
  }
};
