// Esse reducer será responsável por tratar o todas as informações relacionadas as despesas

import { FETCH_CURRENCIES, REMOVE_EXPENSE, SAVE_EXPENSE, TOTAL_PRICE } from '../actions';

const INITIAL_STATE = {
  currencies: [],
  expenses: [
    {
      id: 0,
      value: '10',
      currency: 'USD',
      method: 'Cartão de crédito',
      tag: 'Lazer',
      description: 'Dez dólares',
      // exchangeRates: response,
    },
    {
      id: 1,
      value: '20',
      currency: 'EUR',
      method: 'Dinheiro',
      tag: 'Trabalho',
      description: 'Vinte euros',
      // exchangeRates: response,
    },
  ],
  editor: false,
  idToEdit: 0,
};

export const walletReducer = (state = INITIAL_STATE, action) => {
  switch (action?.type) {
  case FETCH_CURRENCIES:
    return {
      ...state,
      currencies: action.payload.currencies,
    };
  case SAVE_EXPENSE:
    return {
      ...state,
      expenses: [...state.expenses, action.payload.expense],
    };
  case REMOVE_EXPENSE:
    return {
      ...state,
      expenses: state.expenses
        .filter((exp) => JSON.stringify(exp) !== JSON.stringify(action.payload.expense)),
    };
  default:
    return state;
  }
};

const INITIAL_STATE_TOTAL = {
  total: '0',
};

export const totalPriceReducer = (state = INITIAL_STATE_TOTAL, action) => {
  switch (action?.type) {
  case TOTAL_PRICE:
    return {
      ...state,
      total: (Number(state.total) + Number(action.payload.price)).toFixed(2),
    };
  case REMOVE_EXPENSE:
    return {
      ...state,
      total: (Number(state.total)
      - (Math.floor(100
      * (Number(action.payload.expense.exchangeRates[action.payload.expense.currency].ask)
      * Number(action.payload.expense.value))) / 100)).toFixed(2),
    };
  default:
    return state;
  }
};
