import React from 'react';
import { connect } from 'react-redux';
import propTypes, { string } from 'prop-types';
import { saveExpense } from '../actions';

const INITIAL_STATE = {
  value: '',
  description: '',
  currency: 'USD',
  method: 'Dinheiro',
  tag: 'Alimentação',
  price: {},
  button: true,
  total: 0,
};

class ExpenseForm extends React.Component {
  state = INITIAL_STATE;

  fetchPrice = async () => {
    try {
      const response = await fetch('https://economia.awesomeapi.com.br/json/all');
      const json = await response?.json();
      if (json) {
        this.setState({ price: json });
      }
    } catch (error) {
      console.log(error);
    }
  }

  handleChange = ({ target }) => {
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [target.name]: value,
    }, this.buttonValidation);
  }

  buttonValidation = () => {
    const { value } = this.state;
    if (Number(value) > 0) this.setState({ button: false });
    else this.setState({ button: true });
  }

  addExpenseFunc = async () => {
    await this.fetchPrice();

    const { addExpense, expenses } = this.props;
    const { value, description, currency, method, tag, price } = this.state;

    // this.setState({ total: total + (value * price[currency].ask) });

    const expense = {
      id: expenses.length,
      value,
      description,
      currency,
      method,
      tag,
      exchangeRates: price,
    };
    addExpense(expense);

    this.setState(INITIAL_STATE);
  }

  render() {
    const { value, description, currency, method, tag, button } = this.state;
    const { currencies } = this.props;
    const { handleChange, addExpenseFunc } = this;
    return (
      <form>
        <label htmlFor="value-input">
          Valor:
          <input
            type="number"
            data-testid="value-input"
            id="value-input"
            value={ value }
            onChange={ handleChange }
            name="value"
          />
        </label>

        <label htmlFor="description-input">
          Descrição:
          <input
            type="text"
            data-testid="description-input"
            id="description-input"
            value={ description }
            onChange={ handleChange }
            name="description"
          />
        </label>

        <label htmlFor="currencies">
          Moeda:
          <select
            id="currencies"
            value={ currency }
            onChange={ handleChange }
            name="currency"
          >
            {currencies.map((cur) => <option key={ cur }>{cur}</option>)}
          </select>
        </label>

        <label htmlFor="method-input">
          Metódo de pagamento:
          <select
            id="method-input"
            data-testid="method-input"
            value={ method }
            onChange={ handleChange }
            name="method"
          >
            <option>Dinheiro</option>
            <option>Cartão de crédito</option>
            <option>Cartão de débito</option>
          </select>
        </label>

        <label htmlFor="tag-input">
          Tag:
          <select
            id="tag-input"
            data-testid="tag-input"
            value={ tag }
            onChange={ handleChange }
            name="tag"
          >
            <option>Alimentação</option>
            <option>Lazer</option>
            <option>Trabalho</option>
            <option>Transporte</option>
            <option>Saúde</option>
          </select>
        </label>

        <button
          type="button"
          onClick={ addExpenseFunc }
          disabled={ button }
        >
          Adicionar Despesa
        </button>
      </form>
    );
  }
}

const mapStateToProps = (state) => ({
  currencies: state.wallet.currencies,
  expenses: state.wallet.expenses,
});

const mapDispatchToProps = (dispatch) => ({
  addExpense: (expense) => dispatch(saveExpense(expense)),
});

ExpenseForm.propTypes = {
  currencies: propTypes.arrayOf(string),
  addExpense: propTypes.func,
}.isRequired;

export default connect(mapStateToProps, mapDispatchToProps)(ExpenseForm);
