import React from 'react';
import { connect } from 'react-redux';
import propTypes from 'prop-types';
import { attTotal } from '../actions';

class Header extends React.Component {
  componentDidMount() {
    const { expenses, attTotalAction } = this.props;
    // console.log(expenses);
    let total = '0.00';
    expenses.forEach((expense) => {
      const { exchangeRates, currency } = expense;
      total = Number(total)
      + (Math.floor(100 * (expense.value * exchangeRates[currency].ask)) / 100);
    });
    attTotalAction(total);
  }

  render() {
    const { email, total } = this.props;
    return (
      <header style={ { display: 'flex', gap: '10px' } }>
        <img src="https://cdn-icons.flaticon.com/png/512/3757/premium/3757939.png?token=exp=1655925289~hmac=2accb41f19ee23aad4a94cf7e34997ca" style={ { width: '80px' } } alt="Logo da Carteira" />
        <span data-testid="email-field">{email}</span>
        <span data-testid="total-field">{total}</span>
        <span data-testid="header-currency-field">BRL</span>
      </header>
    );
  }
}

const mapStateToProps = (state) => ({
  email: state.user.email,
  total: state.total.total,
  expenses: state.wallet.expenses,
});

const mapDispatchToProps = (dispatch) => ({
  attTotalAction: (total) => dispatch(attTotal(total)),
});

Header.propTypes = {
  email: propTypes.string,
  total: propTypes.number,
  attTotalAction: propTypes.func,
}.isRequired;

export default connect(mapStateToProps, mapDispatchToProps)(Header);
